package curso.java.operadores.aritimeticos;

public class OperadoresAritmeticos01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 10 , y = 3 ; //3
		
		int valor1 =  x/y; //3.0		
		double valor2 = x / y;
		double valor3 = x /(double) y; 
		
		System.out.println(" o valor 1 é   =  " + valor1   );
		System.out.println(" o valor 2 é   =  " + valor2   );
		System.out.println(" o valor 3 é   =  " + valor3   );
		
		
	}

}
