package curso.java.operadores.aritimeticos;

public class OperadoresAritmeticos02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10 , y = 3 ; 
		
		int soma1 = x + y ; 
		String soma2 = x + y + " ";
		String soma3 = " " +  x + y ;
		String soma4 = " " + (x + y) ;
		
		
		System.out.println(" o valor da soma 1 é  =  "  +   soma1);
		System.out.println(" o valor da soma 2 é  =  "  +   soma2);
		System.out.println(" o valor da soma 3 é  =  "  +   soma3);
		System.out.println(" o valor da soma 4 é  =  "  +   soma4);

	}

}
