package curso.java.operadores.aritimeticos;

public class OperadoresAritmeticos03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10 , y = 3;
		
		int divisao = x / y;
		int resto = x % y ; 
		
		System.out.println(" o valor da visão é  = " + divisao );
		System.out.println(" o valor do resto é  = " + resto );
		
		
		

	}

}
